diff --git a/apps/vendor-huobi/src/api/public-api.ts b/apps/vendor-huobi/src/api/public-api.ts
index 03a1468be..c8c6c3801 100644
--- a/apps/vendor-huobi/src/api/public-api.ts
+++ b/apps/vendor-huobi/src/api/public-api.ts
@@ -359,0 +360,25 @@ export function getV2ReferenceCurrencies(params: { currency?: string; authorized
+
+/**
+ * 【现货】获取市场所有交易对的最新行情
+ *
+ * https://www.htx.com/zh-cn/opend/newApiPages/?id=7ec40374-7773-11ed-9966-0242ac110003
+ *
+ */
+export const getSpotMarketTickers = (): Promise<{
+  data: {
+    symbol: string;
+    open: number;
+    high: number;
+    low: number;
+    close: number;
+    amount: number;
+    vol: number;
+    count: number;
+    bid: number;
+    bidSize: number;
+    ask: number;
+    askSize: number;
+  }[];
