+    const route = makeRoute({ urgent_on_severity: 'ERROR' });
+    const group = makeGroup({ status: 'Firing', severity: 'ERROR' });
+    expect(computeWantedUrgentPayload(route, group)).toEqual({ urgent: 'app', userIds: ['u1'] });
+  });
+});
