diff --git a/libraries/utils/src/semaphore.ts b/libraries/utils/src/semaphore.ts
index d0b261c1e..15b8cdd66 100644
--- a/libraries/utils/src/semaphore.ts
+++ b/libraries/utils/src/semaphore.ts
@@ -8,0 +9 @@ interface WaitingRequest {
+  reject: (reason?: any) => void;
@@ -9,0 +11,2 @@ interface WaitingRequest {
+  signal?: AbortSignal;
+  cleanup?: () => void;
@@ -40,0 +44 @@ export interface ISemaphore {
+   * @param signal - 可选的 AbortSignal，用于取消等待
@@ -42 +46,10 @@ export interface ISemaphore {
-  acquire(perms?: number): Promise<void>;
+  acquire(perms?: number, signal?: AbortSignal): Promise<void>;
+
+  /**
+   * 同步获取许可
+   *
+   * 如果当前可用许可不足，则立即抛出错误
+   *
+   * @param perms - 许可数量，默认值为 1
+   */
+  acquireSync(perms?: number): void;
@@ -75 +88 @@ export const semaphore = (semaphoreId: string): ISemaphore => {
-  const acquire = async (perms: number = 1): Promise<void> => {
+  const acquire = async (perms: number = 1, signal?: AbortSignal): Promise<void> => {
@@ -76,0 +90,6 @@ export const semaphore = (semaphoreId: string): ISemaphore => {
+
+    // 如果信号已经触发，立即拒绝
+    if (signal?.aborted) {
+      throw newError('SEMAPHORE_ACQUIRE_ABORTED', { semaphoreId, perms });
+    }
+
@@ -84,2 +103,28 @@ export const semaphore = (semaphoreId: string): ISemaphore => {
-    return new Promise<void>((resolve) => {
-      state!.queue.push({ resolve, perms });
+    return new Promise<void>((resolve, reject) => {
+      const waitingRequest: WaitingRequest = { resolve, reject, perms, signal };
+      state!.queue.push(waitingRequest);
+
+      // 如果提供了 signal，设置 abort 事件监听器
+      if (signal) {
+        const onAbort = () => {
+          // 移除事件监听器
+          signal.removeEventListener('abort', onAbort);
+          // 清理存储的清理函数
+          if (waitingRequest.cleanup) {
