+    const volume = +v.positionAmt;
+    return {
+      position_id: v.symbol,
+      product_id: v.symbol,
+      direction: volume > 0 ? PositionDirection.LONG : PositionDirection.SHORT,
+      volume: Math.abs(volume), // 持仓量取绝对值
+      closable_price: +v.entryPrice,
+      position_price: +v.entryPrice,
