+export const getAccountIncome = (
+  credential: ICredential,
+  params?: {
+    symbol?: string;
+    incomeType?: string;
+    startTime?: number;
+    endTime?: number;
+    page?: number;
+    recvWindow?: number;
+    limit?: number;
+    timestamp?: number;
+  },
+): Promise<IAccountIncomeRecord[]> => {
+  const endpoint = 'https://fapi.binance.com/fapi/v1/income';
