+++ b/apps/virtual-exchange/src/general.ts
@@ -5 +5 @@ import { Terminal } from '@yuants/protocol';
-import { getCredentialBySecretId } from './credential';
+import { getCredentialBySecretId, IExchangeCredential } from './credential';
@@ -111,0 +112,29 @@ terminal.server.provideService<{ order: IOrder; secret_id: string }, void>(
+
+// GetCredential
+terminal.server.provideService<
+  { secret_id: string },
+  {
+    sign: string;
+    credential: IExchangeCredential;
+    credentialId: string;
+  }
+>(
+  'VEX/GetCredential',
+  {
+    type: 'object',
+    required: ['secret_id'],
+    properties: {
+      secret_id: { type: 'string' },
+    },
+  },
+  async (msg) => {
+    const credential = await getCredentialBySecretId(msg.req.secret_id);
+    return {
+      res: {
+        code: 0,
+        message: 'OK',
+        data: credential,
+      },
+    };
+  },
+);

